+	private void setGuided() {
+		SharedPreferences preferences = activity.getSharedPreferences(
+				SHAREDPREFERENCES_NAME, Context.MODE_PRIVATE);
+		Editor editor = preferences.edit();
+		editor.putBoolean("isFirstIn", false);
+		editor.commit();
+	}
