+
+		SimpleDateFormat sdfInput = new SimpleDateFormat("yyyy-MM-dd HH:mm:ss");
+
+		Calendar calendar = Calendar.getInstance();
+		Date date = new Date();
+
+		try {
+			date = sdfInput.parse(dateInput);
