+                content[i] = calendar.get(Calendar.YEAR) + "-"
+                        + calendar.get(Calendar.MONTH) + "-"
+                        + calendar.get(Calendar.DATE);
+                calendar.add(Calendar.DATE, 1);
+            }
+            data = new String[content.length];
+            System.arraycopy(content, 0, data, 0, content.length);
+        }
