+		initListener();
+		mLocationClient.registerLocationListener(myListener); // 注册监听函数
+		initLocation();
+		mLocationClient.start();
+		timer = new Timer();
+		timer.schedule(new TimerTask() {
+			@Override
+			public void run() {
+
+				updateView();
