+	}
+
+	// ��ɫ����
+	public void black(View v) {
+		paint.setColor(Color.BLACK);
+		Toast.makeText(getApplicationContext(), "��ѡ���˺�ɫ", 0).show();
+	}
+
+	// ���ʼӴ�
