+                // TODO Auto-generated method stub
+                switch (checkedId) {
+                    case R.id.radio_gcj02:
+                        tempcoor="gcj02";//国家测绘局标准
+                        break;
+                    case R.id.radio_bd09ll:
+                        tempcoor="bd09ll";//百度经纬度标准
