+					if (Shot.getTag() == 1)
+						Foe.Life -= 2;
+					else
+						Foe.Life--;
+					_Shots.remove(Shot);
+					Shot.removeSelf();
+					if (Foe.Life <= 0) {
+						if (Foe.getCCSprite().getTag() == 2) {
+							ChageScore(1000);
+							AddSpriteAnimal(Foe.getCCSprite().getPosition(),
+									_SmallFoe_Sequence_Path, 52, 52, 3);
