+							ChageScore(6000);
+							AddSpriteAnimal(Foe.getCCSprite().getPosition(),
+									_MiddleFoe_Sequence_Path, 69, 87, 4);
+						}
+						Foe.getCCSprite().removeSelf();
+						_Foes.remove(j);
+					} else {
+						if (Foe.getCCSprite().getTag() == 2) {
+
+						} else {
+							ChageSpriteBack(Foe, true, _MiddleFoe_Path_2, 1);
+							Foe.getCCSprite().removeSelf();
+							_Foes.remove(j);
+						}
+					}
