+*/
+        File file = null; //File(path_str+"/ChatHistory");
+        try {
+            file = new File(get_path.getCanonicalPath() , "ChatHistory");
+        } catch (IOException e) {
+            e.printStackTrace();
+        }
+        //判断历史记录文件是否存在， 不存在则创建
+        if (!file.exists()){
+            try {
+                file.createNewFile();
+                System.out.println("创建文件!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!");
+            } catch (IOException e) {
+                e.printStackTrace();
+            }
+        }
+
+        System.out.println("新建文件之后!");
+        //获取时间+历史记录写入文件 ； 从服务器接收到的信息写入文件
