+		else if(mainView != null){
+			mainView.setThreadFlag(false);
+		}
+		else if(endView != null){
+			endView.setThreadFlag(false);
+		}
+		this.finish();
+	}
+	//getter��setter����
+	public Handler getHandler() {
+		return handler;
+	}
+	public void setHandler(Handler handler) {
+		this.handler = handler;
+	}
