+		0, 65535, 0, 0,	 // 绿色
+		0, 0, 65535, 0,  // 蓝色
+		65535, 65535, 0, 0 //黄色
+	};
+	// 定义三棱椎的4个三角面
+	private byte[] taperFacets = new byte[]{
+		0, 1, 2, //0、1、2三个顶点组成一个面
+		0, 1, 3, //0、1、3三个顶点组成一个面
+		1, 2, 3, //1、2、3三个顶点组成一个面
+		0, 2, 3  //0、2、3三个顶点组成一个面
+	};
+	// 定义立方体的8个顶点
+	float[] cubeVertices = new float[] {
