+		0.5f, -0.5f, -0.5f,
+		-0.5f, -0.5f, -0.5f,
+		-0.5f, 0.5f, -0.5f
+	};
+	// 定义立方体所需要的6个面（一共是12个三角形所需的顶点）
+	private byte[] cubeFacets = new byte[]{
+		0, 1, 2,
