+		gl.glClearColor(0, 0, 0, 0);
+		// 设置阴影平滑模式
+		gl.glShadeModel(GL10.GL_SMOOTH);
+		// 启用深度测试
+		gl.glEnable(GL10.GL_DEPTH_TEST);
+		// 设置深度测试的类型
+		gl.glDepthFunc(GL10.GL_LEQUAL);
+	}
+
+	@Override
+	public void onSurfaceChanged(GL10 gl, int width, int height)
+	{
+		// 设置3D视窗的大小及位置
+		gl.glViewport(0, 0, width, height);
+		// 将当前矩阵模式设为投影矩阵
+		gl.glMatrixMode(GL10.GL_PROJECTION);
+		// 初始化单位矩阵
+		gl.glLoadIdentity();
+		// 计算透视视窗的宽度、高度比
+		float ratio = (float) width / height;
+		// 调用此方法设置透视视窗的空间大小。
+		gl.glFrustumf(-ratio, ratio, -1, 1, 1, 10);
+	}
+
+	// 绘制图形的方法
+	@Override
+	public void onDrawFrame(GL10 gl)
+	{
+		// 清除屏幕缓存和深度缓存
+		gl.glClear(GL10.GL_COLOR_BUFFER_BIT | GL10.GL_DEPTH_BUFFER_BIT);
+		// 启用顶点座标数据
+		gl.glEnableClientState(GL10.GL_VERTEX_ARRAY);
+		// 启用顶点颜色数据
+		gl.glEnableClientState(GL10.GL_COLOR_ARRAY);
+		// 设置当前矩阵模式为模型视图。
+		gl.glMatrixMode(GL10.GL_MODELVIEW);
+		// --------------------绘制第一个图形---------------------
+		// 重置当前的模型视图矩阵
+		gl.glLoadIdentity();
+		gl.glTranslatef(-0.6f, 0.0f, -1.5f);
+		// 沿着Y轴旋转
+		gl.glRotatef(rotate, 0f, 0.2f, 0f);
+		// 设置顶点的位置数据
+		gl.glVertexPointer(3, GL10.GL_FLOAT, 0, taperVerticesBuffer);
+		// 设置顶点的颜色数据
+		gl.glColorPointer(4, GL10.GL_FIXED, 0, taperColorsBuffer);
+		// 按taperFacetsBuffer指定的面绘制三角形
+		gl.glDrawElements(GL10.GL_TRIANGLE_STRIP
+			, taperFacetsBuffer.remaining(),
+			GL10.GL_UNSIGNED_BYTE, taperFacetsBuffer);
+		// --------------------绘制第二个图形---------------------
+		// 重置当前的模型视图矩阵
+		gl.glLoadIdentity();
+		gl.glTranslatef(0.7f, 0.0f, -2.2f);
+		// 沿着Y轴旋转
+		gl.glRotatef(rotate, 0f, 0.2f, 0f);
+		// 沿着X轴旋转
+		gl.glRotatef(rotate, 1f, 0f, 0f);
+		// 设置顶点的位置数据
+		gl.glVertexPointer(3, GL10.GL_FLOAT, 0, cubeVerticesBuffer);
+		// 不设置顶点的颜色数据，还用以前的颜色数据
+		// 按cubeFacetsBuffer指定的面绘制三角形
+		gl.glDrawElements(GL10.GL_TRIANGLE_STRIP
+			, cubeFacetsBuffer.remaining(),
+			GL10.GL_UNSIGNED_BYTE, cubeFacetsBuffer);
+
+		// 绘制结束
+		gl.glFinish();
+		gl.glDisableClientState(GL10.GL_VERTEX_ARRAY);
+		// 旋转角度增加1
+		rotate+=1;
+	}
+	// 定义一个工具方法，将int[]数组转换为OpenGL ES所需的IntBuffer
+	private IntBuffer intBufferUtil(int[] arr)
+	{
+		IntBuffer mBuffer;
+		// 初始化ByteBuffer，长度为arr数组的长度*4，因为一个int占4个字节
+		ByteBuffer qbb = ByteBuffer.allocateDirect(arr.length * 4);
+		// 数组排列用nativeOrder
+		qbb.order(ByteOrder.nativeOrder());
+		mBuffer = qbb.asIntBuffer();
+		mBuffer.put(arr);
+		mBuffer.position(0);
+		return mBuffer;
+	}
+	// 定义一个工具方法，将float[]数组转换为OpenGL ES所需的FloatBuffer
+	private FloatBuffer floatBufferUtil(float[] arr)
+	{
+		FloatBuffer mBuffer;
+		// 初始化ByteBuffer，长度为arr数组的长度*4，因为一个int占4个字节
+		ByteBuffer qbb = ByteBuffer.allocateDirect(arr.length * 4);
+		// 数组排列用nativeOrder
+		qbb.order(ByteOrder.nativeOrder());
+		mBuffer = qbb.asFloatBuffer();
+		mBuffer.put(arr);
+		mBuffer.position(0);
+		return mBuffer;
+	}	
+}

