+                final boolean result = installHelper.install(apkPath);
+                runOnUiThread(new Runnable() {
+                    @Override
+                    public void run() {
+                        if (result) {
+                            Toast.makeText(MainActivity_net1314080903112.this, "安装成功！", Toast.LENGTH_SHORT).show();
