+
+public class Net1314080903143ActivityDiaryEdit extends Activity {
+
+	private EditText mTitleText;
+	private EditText mBodyText;
+	private Long mRowId;
+	private Net1314080903143DiaryDbAdapter mDbHelper;
+	private Context mcontext=this;
+
+	@Override
+	protected void onCreate(Bundle savedInstanceState) {
+		super.onCreate(savedInstanceState);
+		mDbHelper = new Net1314080903143DiaryDbAdapter(this);
+		mDbHelper.open();
+		setContentView(R.layout.activity_net1314080903143diary_edit);
+
+		mTitleText = (EditText) findViewById(R.id.title);
+		mBodyText = (EditText) findViewById(R.id.body);
+
+		Button confirmButton = (Button) findViewById(R.id.confirm);
+		Button deletButton = (Button) findViewById(R.id.delet);
+
+		mRowId = null;
+		// ÿһ��intent�����һ��Bundle�͵�extras���ݡ�
+		Bundle extras = getIntent().getExtras();
+		if (extras != null) {
+			String title = extras.getString(Net1314080903143DiaryDbAdapter.KEY_TITLE);
+			String body = extras.getString(Net1314080903143DiaryDbAdapter.KEY_BODY);
+			mRowId = extras.getLong(Net1314080903143DiaryDbAdapter.KEY_ROWID);
+
