+            @Override
+            public void onClick(View v) {
+                if (mLayoutLoading.getVisibility() == View.GONE) ;
+                {
+                    mLayoutLoading.setVisibility(View.VISIBLE);
+                }
+            }
+        });
+
+
+
+        if (msgId !=-1)
+        {
