+	public Net1314080903142Playground(Context context) {
+		super(context);
+		getHolder().addCallback(callback);
+		matrix = new Net1314080903142Dot[ROW][COL];
+		for (int i = 0; i < ROW; i++) {
+			for (int j = 0; j < COL; j++) {
+				matrix[i][j] = new Net1314080903142Dot(j, i);
