+public class Net1314080903233PinBall extends Activity
+{
+	// ����Ŀ��
+	private int tableWidth;
+	// ����ĸ߶�
+	private int tableHeight;
+	// ���ĵĴ�ֱλ��
+	private int racketY;
+	// ���涨�����ĵĸ߶ȺͿ��
+	private final int RACKET_HEIGHT = 20;
+	private final int RACKET_WIDTH = 70;
+	// С��Ĵ�С
+	private final int BALL_SIZE = 12;
+	// С������������ٶ�
+	private int ySpeed = 10;
+	Random rand = new Random();
+	// ����һ��-0.5~0.5�ı��ʣ����ڿ���С������з���
+	private double xyRate = rand.nextDouble() - 0.5;
+	// С�����������ٶ�
+	private int xSpeed = (int) (ySpeed * xyRate * 2);
+	// ballX��ballY���С������
+	private int ballX = rand.nextInt(200) + 20;
+	private int ballY = rand.nextInt(10) + 20;
+	// racketX������ĵ�ˮƽλ��
+	private int racketX = rand.nextInt(200);
+	// ��Ϸ�Ƿ��������
+	private boolean isLose = false;
+
+	@Override
+	public void onCreate(Bundle savedInstanceState)
+	{
+		super.onCreate(savedInstanceState);
+		// ȥ��ڱ���
+		requestWindowFeature(Window.FEATURE_NO_TITLE);
+		// ȫ����ʾ
+		getWindow().setFlags(WindowManager.LayoutParams.FLAG_FULLSCREEN,
+			WindowManager.LayoutParams.FLAG_FULLSCREEN);
+		// ����GameView���
+		final GameView gameView = new GameView(this);
+		setContentView(gameView);
+		// ��ȡ���ڹ�����
+		WindowManager windowManager = getWindowManager();
+		Display display = windowManager.getDefaultDisplay();
+		DisplayMetrics metrics = new DisplayMetrics();
+		display.getMetrics(metrics);
+		// �����Ļ��͸�
+		tableWidth = metrics.widthPixels;
+		tableHeight = metrics.heightPixels;
+		racketY = tableHeight - 80;
+		final Handler handler = new Handler()
+		{
+			public void handleMessage(Message msg)
+			{
+				if (msg.what == 0x123)
+				{
+					gameView.invalidate();
+				}
+			}
+		};
+
+		gameView.setOnKeyListener(new OnKeyListener() // ��
+		{
+			@Override
+			public boolean onKey(View source, int keyCode, KeyEvent event)
+			{
+				// ��ȡ���ĸ����¼�
+				switch (event.getKeyCode())
+				{
+					// ���Ƶ�������
+					case KeyEvent.KEYCODE_A:
+						if (racketX > 0) racketX -= 10;
+						break;
+					// ���Ƶ�������
+					case KeyEvent.KEYCODE_D:
+						if (racketX < tableWidth - RACKET_WIDTH) racketX += 10;
+						break;
+				}
+				// ֪ͨgameView����ػ�
+				gameView.invalidate();
+				return true;
+			}
+		});
+		final Timer timer = new Timer();
+		timer.schedule(new TimerTask() // ��
+			{
+
+				@Override
+				public void run()
+				{
+					// ���С������߱߿�
+					if (ballX <= 0 || ballX >= tableWidth - BALL_SIZE)
+					{
+						xSpeed = -xSpeed;
+					}
+					// ���С��߶ȳ���������λ�ã��Һ��������ķ�Χ֮�ڣ���Ϸ����
+					if (ballY >= racketY - BALL_SIZE
+							&& (ballX < racketX || ballX > racketX
+									+ RACKET_WIDTH))
+					{
+						timer.cancel();
+						// ������Ϸ�Ƿ��������Ϊtrue��
+						isLose = true;
+					}
+					// ���С��λ������֮�ڣ��ҵ�������λ�ã�С�򷴵�
+					else if (ballY <= 0
+						|| (ballY >= racketY - BALL_SIZE
+							&& ballX > racketX && ballX <= racketX
+							+ RACKET_WIDTH))
+					{
+						ySpeed = -ySpeed;
+					}
+					// С��������
+					ballY += ySpeed;
+					ballX += xSpeed;
+					// ������Ϣ��֪ͨϵͳ�ػ����
+					handler.sendEmptyMessage(0x123);
+				}
+			}, 0, 100);
+	}
+
+	class GameView extends View
+	{
+		Paint paint = new Paint();
+
+		public GameView(Context context)
+		{
+			super(context);
+			setFocusable(true);
+		}
+
+		// ��дView��onDraw������ʵ�ֻ滭
+		public void onDraw(Canvas canvas)
+		{
+			paint.setStyle(Paint.Style.FILL);
+			// ����ȥ���
+			paint.setAntiAlias(true);
+			// �����Ϸ�Ѿ�����
+			if (isLose)
+			{
+				paint.setColor(Color.RED);
+				paint.setTextSize(40);
+				canvas.drawText("��Ϸ�ѽ���", 50, 200, paint);
+			}
+			// �����Ϸ��δ����
+			else
+			{
+				// ������ɫ��������С��
+				paint.setColor(Color.rgb(240, 240, 80));
+				canvas.drawCircle(ballX, ballY, BALL_SIZE, paint);
+				// ������ɫ������������
+				paint.setColor(Color.rgb(80, 80, 200));
+				canvas.drawRect(racketX, racketY, racketX + RACKET_WIDTH,
+						racketY + RACKET_HEIGHT, paint);
+			}
+		}
+	}
+}
