+        editor.commit();
+    }
+
+    /**
+     * 读取设置值
+     */
+    private void showValueToPreferences() {
+        if (mSharedPref != null) {
+            String showItems = mSharedPref.getString(SHOW_ITEMS, "");
+            showItems = "显示条数: " + showItems;
+            ((TextView) findViewById(R.id.text_content)) //
+                    .setText(showItems.toCharArray(), 0, showItems.length());
