+public class ClientThread implements Runnable {
+    private Socket      s;
+    private Handler     handler;        //定义向UI发送消息的Handler对象
+    public Handler     revHandler;     //定义接收UI线程消息的Handler对象
+    BufferedReader       br =null;        //输入流
+    OutputStream         os = null;       //输出流
+
+    public ClientThread(Handler handler)
+    {
+        this.handler = handler;
+    }
+
+    public void run()
+    {
+        try {
+            System.out.println("Socket before!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!");
+            s = new Socket("192.168.240.19", 30000);
+            br = new BufferedReader((new InputStreamReader(s.getInputStream())));
+            os = s.getOutputStream();       //为什么br不是类似这样？
+
+            //启动一条子线程来读取服务器响应的数据
+            new Thread()
+            {
+                @Override
+                public void run()
+                {
+                    String content = null;
+                    //不断读取Socket输入流中的内容
+                    try
+                    {
+                        while((content = br.readLine()) != null)
+                        {
+                            //读到来自服务器的数据后， 发送消息通知程序
+                            //界面显示该数据
+                            Message msg = new Message();
+                            msg.what = 0x123;                               //?????
+                            msg.obj = content;                              //????
+                            handler.sendMessage(msg);                      //???
+                        }
+                    }
+                    catch (IOException e)
+                    {
+                        e.printStackTrace();
+                    }
+                }
+            }.start();                                                     //????
+
+            //当前线程初始化
+            Looper.prepare();                                             //????
+            revHandler = new Handler()
+            {
+                @Override
+                public void handleMessage(Message msg)
+                {
+                    //接收到UI线程中用户输入的数据
+                    if (msg.what == 0x345)
+                    {
+                        //将用户在文本框输入的内容写入网络
+                        try
+                        {
+                            os.write((msg.obj.toString() + "\r\n").getBytes("utf-8"));
+                            //并写到历史文件，格式：时间+【名称】+ 消息
+                        }
+                        catch (Exception e)
+                        {
+                            e.printStackTrace();
+                        }
+                    }
+                }
+            };
+            //启动Looper
+            Looper.loop();
+        }
+        catch (SocketTimeoutException e1)
+        {
+            System.out.println("网络连接超时!!!");
+        }
+        catch (Exception e)
+        {
+            e.printStackTrace();
+        }
+    }
+}

