+    private void init() {
+
+        setColumnCount(4);
+        setBackgroundColor(0xffbbada0);
+
+        setOnTouchListener(new OnTouchListener() {
+            private float startX, startY, offsetX, offsetY;
+
+            @Override
+            public boolean onTouch(View v, MotionEvent event) {
+                switch (event.getAction()) {
+                    case MotionEvent.ACTION_DOWN:
+                        startX = event.getX();
+                        startY = event.getY();
+                        break;
+                    case MotionEvent.ACTION_UP:
+                        offsetX = event.getX() - startX;
+                        offsetY = event.getY() - startY;
+                        if (Math.abs(offsetX) > Math.abs(offsetY)) {
+                            if (offsetX < -5) {
+                                swipeLeft();
+                            } else if (offsetX > 5) {
+                                swipeRight();
+                            }
+                        } else {
+                            if (offsetY > 5) {
+                                swipeUp();
+                            } else if (offsetY < -5) {
+                                swipeDown();
+                            }
+                        }
+                        break;
+                }
+
+                return true;
+            }
+        });
+
+    }
+
+    @Override
+    protected void onSizeChanged(int w,int h,int oldw,int oldh){
+        super.onSizeChanged(w, h, oldw, oldh);
+        int cardWeith = (Math.min(w,h)-10)/4;
+        addCards(cardWeith,cardWeith);
+        startGame();
+    }
+    private void addCards(int cardWidth,int cardHeith){
+        Card c;
+        for(int y = 0;y<4;y++){
+            for(int x = 0;x<4;x++){
+                c = new Card(getContext());
+                c.setNum(0);
+                addView(c,cardWidth,cardHeith);
+                cardsMap[x][y] = c;
+            }
+        }
+    }
+
+    public void startGame(){
+
+        for (int x=0;x<4;x++){
+            for (int y=0;y<4;y++){
+                cardsMap[x][y].setNum(0);
+            }
+        }
+
+        addRandomNum();
+        addRandomNum();
+    }
+
+
+    public void swipeLeft(){
+        boolean mergen = false;
+        for(int y=0;y<4;y++){
+            for(int x=0;x<4;x++){
+                for(int x1=x+1;x1<4;x1++){
+                    if(cardsMap[x1][y].getNum()>0){
+                        if(cardsMap[x][y].getNum()<=0){
+                            cardsMap[x][y].setNum(cardsMap[x1][y].getNum());
+                            cardsMap[x1][y].setNum(0);
+                            x--;
+                            mergen = true;
+                        }else if(cardsMap[x][y].getNum() == cardsMap[x1][y].getNum()){
+                            cardsMap[x][y].setNum(cardsMap[x1][y].getNum()*2);
+                            cardsMap[x1][y].setNum(0);
+                            //getScore;
+                            homeActivity.addScore(cardsMap[x][y].getNum());
+                            mergen = true;
+                        }
+                        break;
+                    }
+                }
+            }
+        }
+        if(mergen){
+            addRandomNum();
+        }
+    }
+
+    public void swipeRight(){
+        boolean mergen = false;
+
+        for(int y=0;y<4;y++){
+            for(int x=3;x>-1;x--){
+                for(int x1=x-1;x1>-1;x1--){
+                      if(cardsMap[x1][y].getNum()>0){
+                          if(cardsMap[x][y].getNum()<=0){
+                             cardsMap[x][y].setNum(cardsMap[x1][y].getNum());
+                             cardsMap[x1][y].setNum(0);
+                             x++;
+                              mergen = true;
+                          }else if(cardsMap[x][y].getNum() == cardsMap[x1][y].getNum()){
+                             cardsMap[x][y].setNum(cardsMap[x1][y].getNum()*2);
+                             cardsMap[x1][y].setNum(0);
+                             homeActivity.addScore(cardsMap[x][y].getNum());
+                             mergen = true;
+                          }
+                          break;
