+			int newX = 1 + RNG.nextInt(mXTileCount - 2);
+			int newY = 1 + RNG.nextInt(mYTileCount + 5);
+			//int newX=1;
+			//int newY=1;
+			newCoord = new Coordinate(newX, newY);
+
+			// Make sure it's not already under the snake 
+			boolean collision = false;
+			int snakelength = mSnakeTrail.size();
+			for (int index = 0; index < snakelength; index++) {
+				if (mSnakeTrail.get(index).equals(newCoord)) {
+					collision = true;
+				}
+			}
+			found = !collision;
+		}
+		if (newCoord == null) {
+			Log.e(TAG, "Somehow ended up with a null newCoord!");
+		}
+		mAppleList.add(newCoord);
+	}
+
+	//ˢ����Ϸ״̬��ÿ����Ϸ����ĸ��¡���Ϸ��ݵĸ��£�����������update()���ɵġ� 
+	public void update() {
+		if (mMode == RUNNING) {
+			long now = System.currentTimeMillis();
+
+			if (now - mLastMove > mMoveDelay) {//�����Ƕ�������Ϸ�տ�ʼʱ��������ƶ����ʵĿ��� 
+				clearTiles();//��ս��滭��
+				updateWalls();//���»���ǽ��
+				updateSnake();//���ߵ� ��Ϸ�߼� �Ĵ��� �Լ�����  
+				updateApples();//�Թ��ӵ� ��Ϸ�߼� �Ĵ��� �Լ����� 
+				mLastMove = now;
+			}
+			mRedrawHandler.sleep(mMoveDelay);//����Handler���� ��ʱˢ�µĿ��ƣ�����mMoveDelay�����ߵ��ƶ��ٶȣ�
+		}
+
+	}
+
+	//��setTile����ǽ�� 
+	private void updateWalls() {
+		for (int x = 0; x < mXTileCount; x++) {//������
+			setTile(GREEN_STAR, x, 3);
+			setTile(GREEN_STAR, x, mYTileCount - 1);
+		}
+		for (int y = 3; y < mYTileCount - 1; y++) {//������
+			setTile(GREEN_STAR, 0, y);
+			setTile(GREEN_STAR, mXTileCount - 1, y);
+		}
+	}
+   //���ƹ���
+	private void updateApples() {
+		for (Coordinate c : mAppleList) {
+			setTile(YELLOW_STAR, c.x, c.y);
+		}
+	}
+
+	private void updateSnake() {
+		boolean growSnake = false;//�Թ���ӵ��߻᳤�����������Ϊ��ı�ǡ�
+
+		Coordinate head = mSnakeTrail.get(0);//ͷ������Ҫ��ֻ��ͷ�����������
+		Coordinate newHead = new Coordinate(1, 1);//����һ��һ����ǰ�ƣ�Ҳ����newHead������ֻ���β����ӡ�
+
+		mDirection = mNextDirection;
+
+		switch (mDirection) {
+		case EAST: {
+			newHead = new Coordinate(head.x + 1, head.y);
+			break;
+		}
+		case WEST: {
+			newHead = new Coordinate(head.x - 1, head.y);
+			break;
+		}
+		case NORTH: {
+			newHead = new Coordinate(head.x, head.y - 1);
+			break;
+		}
+		case SOUTH: {
+			newHead = new Coordinate(head.x, head.y + 1);
+			break;
+		}
+		}
+		//ײǽ���  
+		if ((newHead.x < 1) || (newHead.y < 4) || (newHead.x > mXTileCount - 2)
+				|| (newHead.y > mYTileCount - 2)) {
+			setMode(LOSE);
+			return;
+
+		}
+		//ײ�Լ����
+		int snakelength = mSnakeTrail.size();
+		for (int snakeindex = 0; snakeindex < snakelength; snakeindex++) {
+			Coordinate c = mSnakeTrail.get(snakeindex);
+			if (c.equals(newHead)) {
+				setMode(LOSE);
+				return;
+			}
+		}
+		//�Թ��Ӽ��  
+		int applecount = mAppleList.size();
+		for (int appleindex = 0; appleindex < applecount; appleindex++) {
+			Coordinate c = mAppleList.get(appleindex);
+			if (c.equals(newHead)) {
+				mAppleList.remove(c);
+				addRandomApple();
+
+				mScore++;
+				mMoveDelay *= 0.9;
+
+				growSnake = true;
+			}
+		}
+		// push a new head onto the ArrayList and pull off the tail  
+        //ǰ��  
+		mSnakeTrail.add(0, newHead);
+		if (!growSnake) {
+			mSnakeTrail.remove(mSnakeTrail.size() - 1);
+		}
+		//�����µ�����  
+		int index = 0;
+		for (Coordinate c : mSnakeTrail) {
+			if (index == 0) {
+				setTile(YELLOW_STAR, c.x, c.y);
+			} else {
+				setTile(RED_STAR, c.x, c.y);
+			}
+			index++;
+		}
+
+	}
+
+	//����������ࡣ�ܼ򵥵Ĵ洢XY��ꡣ 
+	private class Coordinate {
+		public int x;
+		public int y;
+
+		public Coordinate(int newX, int newY) {
+			x = newX;
+			y = newY;
+		}
+
+		public boolean equals(Coordinate other) {
+			if (x == other.x && y == other.y) {
+				return true;
+			}
+			return false;
+		}
+
+		@Override
+		public String toString() {
+			return "Coordinate: [" + x + "," + y + "]";
+		}
+	}
+
+}

