+                    time_text.setText("Time:" + String.format("%.2f", counter/60) + "min");
+                    if (counter >= 3600){
+                        time_text.setText("Time:" + String.format("%.2f", counter/3600) + "hours");
+                    }
+                }
+
+            }
+        };
+
+
+        time.schedule(new TimerTask() {
+            @Override
