+        if(level == 4){
+            r=5;
+            c=6;
+        }
+        if(level == 5){
+            r=6;
+            c=6;
+        }
+
+
+
+        handler = new CardsUpdater();
+        loadanimalPics();
+        setContentView(R.layout.activity_new_game18);
