+    class ButtonListener implements View.OnClickListener {
+
+        @Override
+        public void onClick(View v) {
+
+            synchronized (locker) {
+                if(cardOne!=null && cardTwo != null){
+                    return;
+                }
+                int id = v.getId();
+                int x = id/100;
