+            }
+
+
+        }
+
+    }
+
+    class CardsUpdater extends Handler {
+
+        @Override
+        public void handleMessage(Message msg) {
+            synchronized (locker) {
+                checkCards();
+            }
+        }
+        public void checkCards(){
+            if(cards[cardTwo.x][cardTwo.y] == cards[cardOne.x][cardOne.y]){
+                cardOne.button.setVisibility(View.INVISIBLE);
