+	public boolean doMatch()//判断逻辑
+	{
+		if(xiaochu[0]!=100)
+			clearArray();
+		int count=0;
+		int arrayPoint=0;
+		//横查询
+		for(int i=0;i<8;i++)
+		{
+			for(int j=0;j<6;)
+			{
+				while(j+1<8&&gameMatrix[i*8+j]==gameMatrix[i*8+j+1])
+				{
+					j++;
+					count++;
+				}
+				if(count>=2)
+				{
+					for(;count>=0;count--)
+					{
+						xiaochu[arrayPoint]=i*8+j-count;
+						arrayPoint++;
+					}
+				}
+				count=0;
+				j++;
+			}
+		}
+
+		//竖查询
+		for(int i=0;i<8;i++)
+		{
+			for(int j=0;j<6;)
+			{
+				while(j+1<8&&gameMatrix[j*8+i]==gameMatrix[(j+1)*8+i])
+				{
+					j++;
+					count++;
+				}
+				if(count>=2)
+				{
+					for(;count>=0;count--)
+					{
+						xiaochu[arrayPoint]=(j-count)*8+i;
+						arrayPoint++;
+					}
+				}
+				count=0;
+				j++;
+			}
+		}
+
+		serialArray();//数列化数组
+		if(xiaochu[0]==100)
+			return false;
+		else
+		{
+			doRemove();
+			return true;
+		}
+	}
+
+
+	//这个是match之后的消除动画已经新的水晶的插入
+	private void doRemove()
+	{
