+		this.bw=bw;
+	}
+	
+	public int getBw(){
+		return bw;
+	}
+	
+	public void add(Coordinate c){
+		block.add(c);
+	}
+	
+	public void addAir(int air){
+		airCount+=air;
+	}
+	
+	public boolean isLive(){
+		if(airCount>0 && block.size()>0)return true;
+		return false;
+	}
+	
+	public void each(Function f){
+		for(Coordinate c:block){
+			f.apply(c);
+		}
+	}
+}

