+					;
+//			for (int i = 6; i < split.length; i++) {
+//				result.append(" ").append(split[i]);
+//			}
+			return result.toString();
+		}
+		catch (Exception e) {
+			Net1314080903111_Log.e(TAG, e.getMessage(), e);
+			return line;
