+						Net1314080903237_ContactColumn._ID
+										  + "=" + contactID 
+										  + (!TextUtils.isEmpty(where) ? " AND (" + where + ")" : ""),
+										  selectionArgs);
+				break;
+			default:
+				throw new IllegalArgumentException("Unsupported URI: " + uri);
+		}
+		getContext().getContentResolver().notifyChange(uri, null);
+		return count;
+	}
