+	public void Hero_Init() {
+		InputStream is = this.getClass().getResourceAsStream(
+				"/assets/img/Plane_1.png");
+		Bitmap temp = BitmapFactory.decodeStream(is);
+		img_hero = GameUtils.ZomeBitamp(temp, GameUtils.IMG_ENLARGE,
