+			if (MainGame.Point_x < this.hero_x) {
+				this.hero_x -= this.hero_spreed;
+				this.hero_row = 2;
+			}
+			// 上
+			if (MainGame.Point_y < this.hero_y) {
+				this.hero_y -= this.hero_spreed;
+			}
+			// 右
+			if (MainGame.Point_x > this.hero_x + this.hero_w) {
+				this.hero_x += this.hero_spreed;
+				this.hero_row = 0;
+			}
+			// 下
