+		invalidate(selRect);
+		selX = Math.min(Math.max(x, 0), 9);
+		selY = Math.min(Math.max(y, 0), 9);
+		getRect(selX, selY, selRect);
+		invalidate(selRect);
+	}
+
+	private void getRect(int x, int y, Rect rect) {
+		rect.set((int) (x * width), (int) (y * height),
+				(int) (x * width + width), (int) (y * height + height));
+	}
+
+	@Override
