+package edu.hzuapps.androidworks.homeworks.net1313071001221;
+
+import android.content.Context;
+import android.hardware.Sensor;
+import android.hardware.SensorEvent;
+import android.hardware.SensorEventListener;
+import android.hardware.SensorManager;
+import android.widget.Toast;
+
+public class Net_1313071001221_ShakeListener implements SensorEventListener {
+    private static final int SPEED_SHRESHOLD=300;
+    private static final int UPTATE_INTERVAL_TIME=70;
+    private SensorManager sensorManager;
+    private Sensor sensor;
+    private OnShakeListener onShakeListener;
+    private Context mContext;
+    private long lastUpdateTime;
+    private float lastX;
+    private float lastY;
+    private float lastZ;
+    public Net_1313071001221_ShakeListener(Context c){
+        mContext=c;
+        start();
+    }
+
+    public void start(){
+        sensorManager=(SensorManager) mContext.getSystemService(Context.SENSOR_SERVICE);
+        if(sensorManager!=null){
+            sensor=sensorManager.getDefaultSensor(Sensor.TYPE_ACCELEROMETER);
+        }
+
+        if(sensor!=null){
+            sensorManager.registerListener(this,sensor,
+                    SensorManager.SENSOR_DELAY_GAME);
+        }else{
+            Toast.makeText(mContext,"您的手机不支持该功能",Toast.LENGTH_SHORT).show();
+        }
+    }
+
+    public void onSensorChanged(SensorEvent event){
+        long currentUpdateTime=System.currentTimeMillis();
+        long timeInterval=currentUpdateTime-lastUpdateTime;
+        if(timeInterval<UPTATE_INTERVAL_TIME)
+            return;
+        lastUpdateTime=currentUpdateTime;
+        float x=event.values[0];
+        float y=event.values[1];
+        float z=event.values[2];
+        float deltaX=x-lastX;
+        float deltaY=y-lastY;
+        float deltaZ=z-lastZ;
+        lastX=x;
+        lastY=y;
+        lastZ=z;
+        double speed=Math.sqrt(deltaX*deltaX+deltaY*deltaY+deltaZ*deltaZ)/
+                timeInterval*10000;
+        if(speed>=SPEED_SHRESHOLD){
+            onShakeListener.onShake();
+        }
+    }
+
+    public interface OnShakeListener{
+        public void onShake();
+    }
+
+    public void stop(){
+        sensorManager.unregisterListener(this);
+    }
+
+    public void onAccuracyChanged(Sensor sensor,int accuracy){
+    }
+
+    public void setOnShakeListener(OnShakeListener listener){
+        onShakeListener=listener;
+    }
+}

