+				
+			}
+			hs.setAttribute("gradesList", gl);
+		} catch (Exception e) {
+			System.out.print(e.getMessage());
+		}
+		
+		response.sendRedirect("1414080902225/ShowAssessmentInfo.jsp");
+		
+	}
