+            button.setVisibility(View.GONE);/*如果是学生用户 让布置作业按钮隐藏*/
+            i="user";
+        }
+        WorkAdapter adapter=new WorkAdapter(Net1414080903110_WorkListActivity.this,R.layout.work_item,work_infoList);//实例化WorkAdapter适配器
